  //const baseUrl = "http://localhost:9090";

  render() {
    return (
      <section>
        <h3>Create a Folder</h3>
        <form onSubmit={this.handleButtonSubmit}>
          <label htmlFor="folder-input">Name</label>
          <input
            type="text"
            onChange={e => this.setFolderName(e.target.value)}
            id="folder-Input"
