import React from 'react';
import Nav from './Nav';
import Header from './Header'
import './FolderRoute.css'
import Context from './Context'



export default class FolderRoute extends React.Component {
  static contextType = Context;

  handleDeleteButton = e => {
    const baseUrl = "http://localhost:9090";
    console.log(this.props)
    e.preventDefault();
    const noteId = this.props.match.params.noteId;
    console.log(noteId)

    fetch(`${baseUrl}/notes/${noteId}`, {
      method: "DELETE",
      headers: {
        "content-type": "application/json"
      }
    })
    .then(response => {
      if (!response.ok) {
        return response.json().then(e => Promise.reject(e))
      }
      return response.json();
    })
    .then(() => {
      this.props.history.push('/')
      this.context.deleteNote(noteId)

    })
    .catch(error => {
      console.log({error})
    })
  };


  render() {
    const folder = this.props.folders.find(f => f.id === this.props.match.params.folderId)
    const selectedNotes = this.props.notes.filter(notes => notes.folderId === folder.id).map(note =>
      <li>
        <h2>{note.name}</h2>
        <p>Date Modified: {new Date(note.modified).toDateString()}</p>
        <p>{note.content}</p>
        <button 
        type="button" 
        onClick={this.handleDeleteButton}>Delete</button>
      </li>)
    return (
      <main>
        <Header />
        <div className="flexContainer">
          <Nav folders={this.props.folders} />
          <ul>{selectedNotes}</ul>
        </div>
      </main>
    )
  }
}
